import React, { Component } from 'react';

import './Login.css'

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {value: ''};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }


  inputChangeHandler = () => {

  };


  render() {
    return (
      <div className="container mt-5">
        <div className="row">

          <div className="col-4"></div>
          <div className="col-4 form">
            <form >
              <div className="mb-3">
                <label htmlFor="exampleInputEmail1" className="form-label">Email</label>
                <input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" onChange={this.inputChangeHandler}/>
                <div id="emailHelp" className="form-text">Nhập vào email.</div>
              </div>
              <div className="mb-3">
                <label htmlFor="exampleInputPassword1" className="form-label">Password</label>
                <input type="password" className="form-control" id="exampleInputPassword1" />
                <div id="emailHelp" className="form-text">Nhập vào password.</div>

              </div>

              {/* <button class="button rounded-1 primary-bg text-white w-100 btn_3 boxed-btn" type="submit">Login</button> */}
              <a href="#" class="genric-btn btn-block primary large login_button">LOGIN</a>
            </form>
          </div>
        </div>

      </div>
    )
  }
}

export default Login;
