import React, { Component } from 'react'

export default class ItemGioHang extends Component {
    state ={
        itemId: 1
    }
    showStatus =(status) =>{
        if(status == 1) return "Đang thực hiện";
        if(status == 2) return "Đã hoàn tất";

    }
    render() {
        return (
            <div className="table-row">
                <div className="serial">{this.props.data.index}</div>
                <div className="country"> <img src="assets/img/elements/f1.jpg" alt="flag" />{this.props.data.title}</div>
                <div className="visit">{this.props.data.price}</div>
                <div className="visit">{this.props.data.quantity}</div>
                <div className="percentage">
                    {this.props.data.status}
                </div>
            </div>
        )
    }
}
