import React, { Component } from 'react'
import {url} from '../constant';
export default class ItemGioHang extends Component {

    state ={
        itemId: 1,
        
    }
    showStatus =(status) =>{
        if(status === 1) return <p class="text-primary">Đang thực hiện</p>;
        if(status === 2) return <p class="text-success">Đã hoàn tất</p>;
        if(status === -1) return <p class="text-danger">Đã hủy</p>;

    }
    xoaSanPham = (e) =>{
        e.preventDefault();
        if(this.props.data._id){
            console.log();
            
        }
    }
    render() {
        return (
            <div className="table-row">
                <div className="serial">{this.props.data.index}</div>
                <div className="country"> <img src={url + this.props.data.img} alt={this.props.data.img} width= "30px" />{this.props.data.title}</div>
                <div className="visit">{this.props.data.price}</div>
                <div className="visit">{this.props.data.quantity}</div>
                <div className="visit"> 
                    <a href="#" class="genric-btn danger-border">Xóa</a>
                </div>
                
            </div>
        )
    }
}
