import React, { Component, Fragment } from 'react'
import ItemGioHang from '../../ItemGioHang/ItemGioHang';
export default class GioHang extends Component {
    state = {
        products: [],
        token: this.props.token
    };
    componentDidMount() {
        this.setState({ token: this.props.token })
        this.loadProducts();
    }
    loadProducts = () => {
        console.log('Loading cart');

        let method = 'GET';
        let url = 'http://localhost:3001/cart';

        fetch(url, {
            method: method,
            headers: { 'Authorization': 'Bearer ' + this.props.token },
        })
            .then(res => {
                if (res.status !== 200) {
                    throw new Error('Failed to fetch cart.');
                }
                return res.json();
            })
            .then(resData => {
                console.log(resData.products);
                this.setState({
                    products: resData.products,
                });
            })
            .catch(this.catchError);
    };
    showAllProduct = () => {
        if (this.state.products.length > 0) {
            console.log(this.state.products);
            return (
                this.state.products.map(product => {
                    return <p>{product.productId}</p>
                })
            );
        }
    };
    render() {
        return (
            <Fragment>
                <div className="container">
                <div className="section-top-border">
                    <h3 className="mb-30">Table</h3>
                    <div className="progress-table-wrap">
                        <div className="progress-table">

                            <div className="table-head">
                                <div className="serial">#</div>
                                <div className="country">Tên</div>
                                <div className="visit">Giá</div>
                                <div className="visit">Số lượng</div>
                                <div className="percentage">Tình trạng</div>
                            </div>
                            <ItemGioHang data={{index: 1,img: "/images/dog.", price: 10000, title: "Bánh", quantity: 2, status: 2}}></ItemGioHang>
                            
                        </div>
                    </div>
                </div>
                </div>

            </Fragment>
        )
    }
}
