import React, { Component, Fragment } from 'react'
import ItemDonHang from '../ItemDonHang/ItemDonHang';
import { handleCLickCreateOrder } from '../Alert/Alert';
import  { Redirect } from 'react-router-dom'

export default class GioHang extends Component {
    state = {
        orders: [],
        token: this.props.token,
        isUpdate: false,
        redirectOrder: false
    };

    componentDidMount() {
        this.setState({ token: this.props.token });
        this.setState({ isUpdate: false });
        this.loadProducts();
    }
    loadProducts = () => {
        console.log('Loading cart');

        let method = 'GET';
        let url = 'http://localhost:3001/getOrders';

        fetch(url, {
            method: method,
            headers: { 'Authorization': 'Bearer ' + this.props.token },
        })
            .then(res => {
                if (res.status !== 200) {
                    throw new Error('Fetching order Failed.');
                }
                return res.json();
            })
            .then(resData => {
                console.log("resData.products");
                console.log(resData.orders);
                this.setState({
                    orders: resData.orders,
                });
            })
            .catch(this.catchError);
    };
    delete = (i) => {
        var upd = this.state.isUpdate;
        this.setState({
            isUpdate: !upd
        })
        console.log('loaddddddđ');
        setTimeout(this.loadProducts(), 1000);
    }
    showAllProduct = () => {
        if (this.state.orders.length > 0) {
            console.log(this.state.orders);
            let i = 0;
            return (
                this.state.orders.map(order => {
                    // i = i + 1;
                    console.log("order");
                    console.log(order.products);
                    return order.products.map(product => {
                        return <ItemDonHang data={{ _id: product.productId._id, index: i, img: product.productId.imageUrl, price: product.productId.price, title: order.productId.title, quantity: order.quantity }} token={this.props.token} onDelete={this.delete}></ItemDonHang>
                    })
                    // 
                })
            );
        }
    };

    
    showRedirect = ()=>{
        const { redirectOrder } = this.state;
        
        if (redirectOrder) {
            this.setState({
                redirectOrder: false
            })
            return <Redirect to='/giohang'/>;
        }
    }
    render() {
        return (
            <Fragment>
                
                {this.showRedirect()}
                <div className="container">
                    <div className="section-top-border">
                        <h3 className="mb-30">Table</h3>
                        <div className="progress-table-wrap">
                            <div className="progress-table">

                                <div className="table-head">
                                    <div className="serial">#</div>
                                    <div className="country">Tên</div>
                                    <div className="visit">Giá</div>
                                    <div className="visit">Số lượng</div>
                                    <div className="float-right">#</div>
                                </div>
                                {this.showAllProduct()}
                            </div>
                        </div>

                    </div>

                </div>

            </Fragment>
        )
    }
}
